var React = require("react");
var mergeProps = require("../utils/merge-props");
var getChildren = require("../utils/children");

var numbers = "zero,one,two,three,four,five,six,seven,eight,nine,ten,eleven,twelve,thirteen,fourteen,fifteen,sixteen".split(",");

var Grid = React.createClass({

  getDefaultProps:function() {
    return {};
  },

  render:function() {

    var props = mergeProps({classSet:{
      ui:true,
      grid:true
    }},this.props);

    return <div {...props}/>;
  }

});

var Row = React.createClass({
  render:function() {

    var children = getChildren(this);

    var props = mergeProps({classSet:{
      [numbers[children.length]]:true ,
      column:true,
      row:true
    }},this.props);

    return <div {...props}/>;

  }
});

var Col = React.createClass({
  render:function() {

    var props = mergeProps({className:"column"},this.props);

    return <div {...props}/>;

  }
});

Grid.Grid = Grid;
Grid.Row = Row;
Grid.Col = Col;

module.exports = Grid;
