var utils = require("../../utils"),
    React = require("react");

module.exports = React.createClass({
  displayName:"Icon",

  getInitialState:function() {
    return {}
  },

  render:function() {
    var props = this.props;
    var svgProps = {
      width:props.width || "1em",
      height:props.height || "1em",
      color:props.color || "currentColor",
      viewBox:props.viewBox || "0,-1000,1000,1300"
    }

    return (
      <svg class="svg-icon" {...svgProps}>
        <path d={props.path} transform="scale(1,-1)" />
      </svg>
    )
    
  }
})