var utils = require("../utils");
var EventEmitter = require("../event-emitter");
var clientTools = require("../client-tools");


class AddressPicker extends EventEmitter {
  constructor() {
    super();

    this.on("complete cancel error",function() {
      this.off()
    })

    clientTools.invoke("addressPicker",{cb:this.trigger.bind(this)});
  }
}

module.exports = AddressPicker

