var EventEmitter = require("../event-emitter");
var clientTools = require("../client-tools");


class ImagePicker extends EventEmitter {
  constructor(params={}) {
    super();
    this.on("complete cancel error",function() {
      this.off();
    });
    params.cb = this.trigger.bind(this);
    clientTools.invoke("imagePicker",params);
  }
}

module.exports = ImagePicker;

