var utils = require("../utils");
var EventEmitter = require("../event-emitter");
var clientTools = require("../client-tools");


class LocationPicker extends EventEmitter {
  constructor(params={}) {
    super();
    this.on("complete cancel error",function() {
      this.off()
    })
    params.cb = this.trigger.bind(this)
    clientTools.invoke("locationPicker",params);
  }
}

module.exports = LocationPicker;

