const request = require("request");

function post(...args) {
  return new Promise((resolve,reject)=>{
    request.post(...args,(err,...resArgs)=>{
      if (err) {
        err.response = resArgs[0];
        err.body = resArgs[1];
        reject(err);
        return;
      }
      resolve(resArgs);
    });
  });
}

module.exports = {post};
