import {
  SimpleStore,
  loggerPlugin,
  localStoragePlugin,
  //windowSizePlugin,
} from "@ombiel/aek-lib/simple-store";

export class Store extends SimpleStore {
  constructor() {
    super({
      initialState: {
        counter: 0,
      },
      plugins: [
        loggerPlugin(),
        localStoragePlugin("{{screenName}}-store"),
        // windowSizePlugin(),
      ],
    });
  }
}

const store = new Store();
export {store};

export function incrementCounter() {
  store.dispatch({
    name: "INCREMENT_COUNTER",
    path: "counter",
    replace: (oldValue)=>{ return oldValue + 1; }
  });
}
