import {EventEmitter} from "@ombiel/cm-lib";
import clientTools from "../client-tools";


export default class AddressPicker extends EventEmitter {
  constructor() {
    super();

    this.on("complete cancel error",function() {
      this.off();
    });

    clientTools.invoke("addressPicker",{cb: this.trigger.bind(this)});
  }
}
