import {EventEmitter} from "@ombiel/cm-lib";
import clientTools from "../client-tools";


export default class LocationPicker extends EventEmitter {
  constructor(params = {}) {
    super();
    this.on("complete cancel error",function() {
      this.off();
    });
    params.cb = this.trigger.bind(this);
    clientTools.invoke("locationPicker",params);
  }
}
