import React from "react";

import {RouterView as CMRouterView} from "@ombiel/cm-lib";

import {Router} from "../router";

export class AekReactRouter extends Router {
  constructor(opts) {

    if (!opts) { opts = {}; }

    super(opts);

    this.transitionName = opts.transtionName || "slide-horizontal";

  }
}

export function RouterView(props) {
  return <CMRouterView {...props} />;
}

RouterView.defaultProps = {
  Router,
};
