import React from 'react';
import {shallow} from "enzyme";
import {ButtonGroup, Or} from "./button-group";
import {Button} from "./button";

describe("Button Group with no props",()=>{

  const wrapper = shallow(
    <ButtonGroup>
      <Button />
    </ButtonGroup>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have a div with css classes ui and buttons",()=>{
    expect(wrapper.find('div')).toHaveLength(1);
    expect(wrapper.hasClass('ui')).toEqual(true);
    expect(wrapper.hasClass('buttons')).toEqual(true);
  });

  it("should not have any other css classes", ()=>{
    expect(wrapper.prop('className')).toEqual("ui buttons");
  });
});


describe("Button Group with state props",()=>{

  const wrapper = shallow(
    <ButtonGroup
      vertical
      basic
      divided
    >
      <Button />
    </ButtonGroup>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have css class vertical",()=>{
    expect(wrapper.hasClass('vertical')).toEqual(true);
  });

  it("should have css class basic",()=>{
    expect(wrapper.hasClass('basic')).toEqual(true);
  });

  it("should have css class divided",()=>{
    expect(wrapper.hasClass('divided')).toEqual(true);
  });

});

describe("Button Group with fluid prop",()=>{

  const wrapper = shallow(
    <ButtonGroup
      fluid
    >
      <Button />
      <Or />
      <Button />
    </ButtonGroup>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have css class fluid",()=>{
    expect(wrapper.hasClass('fluid')).toEqual(true);
  });

  it("should have css class two",()=>{
    expect(wrapper.hasClass('two')).toEqual(true);
  });

});

describe("Button Group with size prop",()=>{
  const wrapper = shallow(
    <Button
      size="mini"
    />
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have css class mini",()=>{
    expect(wrapper.hasClass('mini')).toEqual(true);
  });

});

describe("Button Group with variation prop",()=>{
  const wrapper = shallow(
    <Button
      variation="positive"
    />
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have css class positive",()=>{
    expect(wrapper.hasClass('positive')).toEqual(true);
  });

});

describe("Or",()=>{
  const wrapper = shallow(
    <Or />
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should be a div with a css class of or",()=>{
    expect(wrapper.find('div')).toHaveLength(1);
    expect(wrapper.hasClass('or')).toEqual(true);
  });

});
