import React from 'react';
import {shallow} from "enzyme";
import Container from "./container";

describe("Standard Container",()=>{
  const wrapper = shallow(
    <Container>
      DisplayText
    </Container>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have one container with css classes container",()=>{
    const containerComponent = wrapper.find('main');
    expect(containerComponent).toHaveLength(1);
    expect(containerComponent.hasClass('container')).toEqual(true);
    expect(containerComponent.text()).toEqual('DisplayText');
  });

  it("should not have any other css classes", ()=>{
    expect(wrapper.find('main').prop('className')).toEqual("container");
  });

});
