import React from 'react';
import {shallow} from "enzyme";
import {FlexContainer, HContainer, VContainer, CContainer, FlexItem} from "./flexbox";
import {ScrollBox} from "./scroll-box";

describe("FlexContainer with no props",()=>{
  const wrapper = shallow(
    <FlexContainer />
  );

  it("should match the snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });
});

describe("FlexContainer with all props",()=>{
  const wrapper = shallow(
    <FlexContainer
      flexDirection="column"
      alignItems="center"
      justifyContent="space-between"
      flexWrap="wrap"
    />
  );

  it("should match the snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });
});

describe("HContainer",()=>{
  const wrapper = shallow(
    <HContainer />
  );

  it("should match the snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });
});

