import "./base.aekcss";
import "./grid.aekcss";
import "./icon.aekcss";


import React from "react";
import mergeProps from "../utils/merge-props";
import getChildren from "../utils/children";

const numbers = "zero,one,two,three,four,five,six,seven,eight,nine,ten,eleven,twelve,thirteen,fourteen,fifteen,sixteen".split(",");

export function Grid(props) {

  const newProps = mergeProps({
    classSet: {
      ui: true,
      grid: true
    }
  },props);

  return <div {...newProps} />;
}

export function Row(props) {

  const children = getChildren(props);

  const newProps = mergeProps({
    classSet: {
      [numbers[children.length]]: true ,
      column: true,
      row: true
    }
  },props);

  return <div {...newProps} />;

}


export function Col(props) {

  var newProps = mergeProps({className: "column"},props);

  return <div {...newProps} />;

}
