import React from 'react';
import {mount} from "enzyme";
import Page from "./page";

describe("Standard Page",()=>{
  const wrapper = mount(
    <Page>
      DisplayText
    </Page>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have one page with css classes page panel",()=>{
    const containerComponent = wrapper.find('div');
    expect(containerComponent).toHaveLength(1);
    expect(containerComponent.hasClass('panel')).toEqual(true);
    expect(containerComponent.hasClass('page')).toEqual(true);
    expect(containerComponent.text()).toEqual('DisplayText');
  });


  it("should not have any other css classes", ()=>{
    expect(wrapper.find('div').prop('className')).toEqual("panel page");
  });

});
