import React from 'react';
import {shallow} from "enzyme";
import Panel from "./panel";

describe("Panel no props",()=>{
  const wrapper = shallow(
    <Panel>
      Panel
    </Panel>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have a div with the class of panel",()=>{
    const panelComponent = wrapper.find('div');
    expect(panelComponent).toHaveLength(1);
    expect(panelComponent.hasClass('panel')).toEqual(true);
    expect(panelComponent.text()).toEqual('Panel');
  });
});

describe("Panel with loading",()=>{
  const wrapper = shallow(
    <Panel loading>
      Panel Loading
    </Panel>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have a div with the class of panel and loading",()=>{
    const panelComponent = wrapper.find('div');
    expect(panelComponent).toHaveLength(1);
    expect(panelComponent.hasClass('panel')).toEqual(true);
    expect(panelComponent.hasClass('loading')).toEqual(true);
    expect(panelComponent.text()).toEqual('Panel Loading');
  });

});

describe("Panel with padding",()=>{
  const wrapper = shallow(
    <Panel padded>
      Panel Padding
    </Panel>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have a div with the class of panel and padding",()=>{
    const panelComponent = wrapper.find('div.panel');
    expect(panelComponent).toHaveLength(1);
    expect(panelComponent.hasClass('panel')).toEqual(true);
    expect(panelComponent.hasClass('padding')).toEqual(true);
    expect(panelComponent.text()).toEqual('Panel Padding');
  });

});
