import React from 'react';
import {shallow} from "enzyme";
import {CMReactRouter, RouterView, AekReactRouter} from "./router";
import {Segment} from "./segment";
import Page from "./page";

describe("RouterView with router prop of CMReactRouter",()=>{

  const router = new CMReactRouter({useHash: false});

  const wrapper = shallow(
    <Segment>
      <RouterView router={router}>
        <Page path="/">
          <Segment />
        </Page>
        <Page path="/page" />
      </RouterView>
    </Segment>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

});

describe("RouterView with router prop of AEKReactRouter",()=>{

  const router = new AekReactRouter({useHash: false});

  const wrapper = shallow(
    <Segment>
      <RouterView router={router}>
        <Page path="/">
          <Segment />
        </Page>
        <Page path="/page" />
      </RouterView>
    </Segment>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

});
