import React from 'react';
import {mount} from "enzyme";
import SelectInput from "./select-input";

describe("SelectInput",()=>{
  const wrapper = mount(
    <SelectInput></SelectInput>
  );

  it("should match snapshot",()=>{
    expect(wrapper).toMatchSnapshot();
  });

  it("should have a div with an icon",()=>{
    const selectInputComponent = wrapper.find("div");
    expect(selectInputComponent).toHaveLength(1);

    const selectInputIconComponent = wrapper.find("i");
    expect(selectInputIconComponent).toHaveLength(1);

    expect(selectInputIconComponent.hasClass('angle')).toEqual(true);
    expect(selectInputIconComponent.hasClass('down')).toEqual(true);
    expect(selectInputIconComponent.hasClass('icon')).toEqual(true);
  });

  it("should have a select with a icon and placeholder",()=>{
    const selectInputComponent = wrapper.find("select");
    expect(selectInputComponent).toHaveLength(1);
    expect(selectInputComponent.prop('icon')).toEqual("angle down");
    expect(selectInputComponent.prop('placeholder')).toEqual("-- Please Select --");
  });

  it("should have an option with the value and text of -- Please Select --",()=>{
    const selectInputComponent = wrapper.find("option");
    expect(selectInputComponent).toHaveLength(1);
    expect(selectInputComponent.prop('value')).toEqual("-- Please Select --");
    expect(selectInputComponent.text()).toEqual('-- Please Select --');
  });
});
